import React, { useState } from "react";

const User = ({ onUpdate }) => {
  const [user, setUser] = useState({});

  const createUser = () => {
    fetch("/createUser", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: user.username }),
    })
      .then((results) => results.json())
      .then((data) => {
        setUser((prevUser) => ({
          ...prevUser,
          userId: data.id,
        }));
        onUpdate(user);
      });
  };

  const setUsername = (evt) => {
    setUser({
      username: evt.target.value,
    });
  };

  if (user.userId) {
    return (
      <div className="userinfo userconnected">
        Connected as <span className="username">{user.username}</span>
      </div>
    );
  } else {
    return (
      <div className="userinfo">
        <input
          className="userinfoinput"
          type="text"
          onChange={(evt) => setUsername(evt)}
        />
        <button onClick={createUser}>Create user</button>
      </div>
    );
  }
};

export default User;
