import React, { useState, useEffect } from "react";
import User from "./User";
import Conversation from "./Conversation";
import nexmoClient from "nexmo-client";
import "./App.css";

const App = () => {
  const [userLoginInfo, setUserLoginInfo] = useState({});
  const login = async () => {
    try {
      let nexmo = new nexmoClient({
        debug: true,
        log_reporter: { enabled: true },
      });
      const sessionData = await nexmo.createSession(userLoginInfo.token);
      console.log("session" + sessionData);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        sessionData: sessionData,
      }));
    } catch (error) {
      console.error("Error in login:", error);
    }
  };

  const getJWT = async (username) => {
    try {
      const response = await fetch("/getJWT", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: username }),
      });
      const data = await response.json();
      console.log("data" + data.jwt);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        token: data.jwt,
      }));
    } catch (error) {
      console.error("Error in getJWT:", error);
    }
  };

  const userUpdated = (user) => {
    if (user.username) {
      getJWT(user.username);
    }
  };

  useEffect(() => {
    try {
      const response = fetch("/getUsers", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });

      console.log("data" + response);
    } catch (error) {
      console.error("Error in getJWT:", error);
    }

    // Use useEffect to watch for changes in userLoginInfo.token
    if (userLoginInfo.token) {
      login();
    }
  }, [userLoginInfo.token]);

  return (
    <div className="nexmo">
      <div className="nexmocontainer">
        <User onUpdate={userUpdated} />
        <Conversation
          sessionData={userLoginInfo.sessionData}
          loggedIn={userLoginInfo.token}
        />
      </div>
    </div>
  );
};

export default App;
