import React, { useState, useEffect } from "react";

const Conversation = ({ sessionData, loggedIn }) => {
  const [conversationData, setConversationData] = useState({});
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState(null);

  const createConversation = () => {
    sessionData.newConversation().then((conv) => {
      conv.join().then((member) => {
        setConversationData({
          conversation: conv,
        });
      });
    });
  };

  const onMessage = (sender, message) => {
    let newMessages = messages.concat({
      key: message.id,
      sender: sender.display_name,
      text: message.body.text,
    });
    setMessages(newMessages);
  };

  const sendInput = (evt) => {
    conversationData.conversation.sendText(input).then(() => {
      setInput(null);
    });
    evt.target.previousSibling.value = "";
  };

  useEffect(() => {
    // componentDidMount logic can go here
  }, []);

  if (conversationData.conversation) {
    return (
      <>
        <div className="conversation">Joined conversation!</div>
        <div className="messages">
          {messages.map((msg) => (
            <p key={msg.key} className="message">
              <b>{msg.sender}:</b>
              {msg.text}
            </p>
          ))}
        </div>
        <div className="input">
          <textarea onBlur={(evt) => setInput(evt.target.value)} />
          <button onClick={(evt) => sendInput(evt)}>Chat</button>
        </div>
      </>
    );
  } else {
    return (
      <div className="userinfo">
        <button
          className="userbutton"
          onClick={createConversation}
          disabled={!loggedIn}
        >
          Start conversation
        </button>
      </div>
    );
  }
};

export default Conversation;
