import React, { useState, useRef } from "react";
import nexmoClient from "nexmo-client";
import "./App.scss";
import User from "./components/User";
import Chatroom from "./components/Chatroom";

const App = () => {
  const [show, setShow] = useState(false);
  const [openConv, setOpenConv] = useState(false);
  const [userLoginInfo, setUserLoginInfo] = useState({});
  const jwtObtainedRef = useRef({});

  const login = async (jwttoken) => {
    try {
      let nexmo = new nexmoClient({
        debug: true,
        log_reporter: { enabled: true },
      });
      const sessionData = await nexmo.createSession(jwttoken);
      console.log("session" + sessionData);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        sessionData: sessionData,
      }));
      setOpenConv(true);
    } catch (error) {
      console.error("Error in usersData:", error);
    }
  };

  const getJWT = async (user) => {
    try {
      if (!jwtObtainedRef.current[user.username]) {
        const response = await fetch("/getJWT", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: user.username }),
        });
        const data = await response.json();
        console.log("data" + data.jwt);
        setUserLoginInfo((prevState) => ({
          ...prevState,
          user: user,
          token: data.jwt,
        }));
        login(data.jwt);
        jwtObtainedRef.current[user.username] = true;
      }
    } catch (error) {
      console.error("Error in getJWT:", error);
    }
  };

  const openChat = (user) => {
    getJWT(user);
  };

  const handleLoginClick = () => {
    setShow("login");
  };
  const handleSignupClick = () => {
    setShow("signup");
  };

  if (openConv) {
    return <Chatroom loggedinuser={user} userLoginInfo={userLoginInfo} />;
  } else {
    return (
      <div className="nexmoapplication">
        <div className="nexmocontainer">
          <div className="btncontainer">
            <button className="button" onClick={handleLoginClick}>
              Login
            </button>
            <button className="button" onClick={handleSignupClick}>
              Signup
            </button>
          </div>
          {show === "signup" ? (
            <div className="signup">
              <User openChatUpdate={openChat} />
            </div>
          ) : (
            <div className="login">
              <h2>Coming soon</h2>
              <p>Login Component</p>
            </div>
          )}
        </div>
      </div>
    );
  }
};

export default App;
