import React, { useState, useEffect } from "react";

import nexmoClient from "nexmo-client";
import "./App.scss";
import User from "./components/User";
import Conversation from "./components/Conversation";

const App = () => {
  const [show, setShow] = useState(false);
  const [userLoginInfo, setUserLoginInfo] = useState({});
  const [isLogin, setIsLogin] = useState(false);
  const [users, setUsers] = useState();
  const login = async () => {
    try {
      let nexmo = new nexmoClient({
        debug: true,
        log_reporter: { enabled: true },
      });
      const sessionData = await nexmo.createSession(userLoginInfo.token);
      console.log("session" + sessionData);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        sessionData: sessionData,
      }));
      setIsLogin(true);
    } catch (error) {
      console.error("Error in usersData:", error);
    }
  };

  const getJWT = async (username) => {
    try {
      const response = await fetch("/getJWT", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: username }),
      });
      const data = await response.json();
      console.log("data" + data.jwt);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        token: data.jwt,
      }));
    } catch (error) {
      console.error("Error in getJWT:", error);
    }
  };

  const userUpdated = (user) => {
    if (user.username) {
      getJWT(user.username);
    }
  };

  const getUsers = async () => {
    try {
      const response = await fetch("/getUsers", {
        method: "GET",
      })
        .then((results) => results.json())
        .then((data) => {
          setUsers(data.users);
          console.log(
            "users : " + JSON.stringify(data.users._embedded.data.users)
          );
        });
    } catch (error) {
      console.error("Error in :", error);
    }
  };

  const handleLoginClick = () => {
    setShow("login");
  };
  const handleSignupClick = () => {
    setShow("signup");
  };

  useEffect(() => {
    if (userLoginInfo.token) {
      login();
      setIsLogin(true);
      getUsers();
    }
  }, [userLoginInfo.token]);

  return (
    <div className="nexmoapplication">
      <div className="nexmocontainer">
        <div className="btncontainer">
          <button className="button" onClick={handleLoginClick}>
            Login
          </button>
          <button className="button" onClick={handleSignupClick}>
            Signup
          </button>
        </div>
        {show == "signup" ? (
          <div className="signup">
            <User onUpdate={userUpdated} />
            {isLogin && (
              <button
                id="btn2"
                className="button"
                disabled={!userLoginInfo.token}
              >
                Start conversation
              </button>
            )}
            {/* <Conversation
              sessionData={userLoginInfo.sessionData}
              loggedIn={userLoginInfo.token}
            /> */}
          </div>
        ) : (
          <div className="login">
            <h2>Component B</h2>
            <p>This is Component B content.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
