import React, { useEffect, useState } from "react";
import Card from "./Card";

function Chatroom({ userLoginInfo }) {
  const [users, setUsers] = useState([]);
  const [member, setMember] = useState();
  const [conversationData, setConversationData] = useState({});
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState(null);

  const createConversation = async (val, userLoginInfo) => {
    try {
      const conv = await userLoginInfo.sessionData.newConversation();
      conv.on("text", onMessage);
      await conv.join();

      setConversationData({
        conversation: conv,
      });

      createMember(val, conv.id); // Pass conversation id directly
    } catch (err) {
      console.error("Error in create Conversation:", err);
    }
  };

  const conversationId = "your_actual_conversation_id";
  const JWT = "your_jwt_token";
  const USER_ID = "user_id_to_invite";

  const createMember = async (val, conversationId) => {
    try {
      const response = await fetch(
        `https://api.nexmo.com/v0.3/conversations/${conversationId}/members`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${JWT}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            user_id: USER_ID,
            action: "invite",
            channel: {
              type: "app",
            },
          }),
        }
      );

      const data = await response.json();
      console.log("Member created:", data);
    } catch (error) {
      console.error("Error creating member:", error);
    }
  };

  // Call the function to create the member

  // const createMember = async (val, conversationId) => {
  //   try {
  //     await fetch("/createMember", {
  //       method: "POST",
  //       headers: { "Content-Type": "application/json" },
  //       body: JSON.stringify({
  //         userId: val.id,
  //         conversationId: conversationId,
  //       }),
  //     })
  //       .then((results) => results.json())
  //       .then((data) => {
  //         console.log("data" + data);
  //       });
  //   } catch (err) {
  //     console.error("Error in create member:", err);
  //   }
  // };

  const onMessage = (sender, message) => {
    let newMessages = messages.concat({
      key: message.id,
      sender: sender.display_name,
      text: message.body.text,
    });
    setMessages((prevmessage) => ({
      ...prevmessage,
      newMessages,
    }));
  };

  const sendInput = (evt) => {
    conversationData.conversation.sendText(input).then(() => {
      setInput(null);
    });
    evt.target.previousSibling.value = "";
  };

  const ncard = (val) => {
    return (
      <Card
        username={val.name}
        onClick={() => {
          setMember(val);
        }}
      />
    );
  };
  useEffect(() => {
    if (member) createConversation(member, userLoginInfo);
  }, [member]);

  useEffect(() => {
    getUsers();
  }, []);
  const getUsers = async () => {
    try {
      const response = await fetch("/getUsers", {
        method: "GET",
      })
        .then((results) => results.json())
        .then((data) => {
          setUsers(data.users._embedded.data.users);
        });
    } catch (error) {
      console.error("Error in :", error);
    }
  };

  return (
    <div className="chatroom">
      <div className="leftroom">
        <div className="header">{userLoginInfo.user.username}</div>
        <div className="leftcontainer">{users.map(ncard)}</div>
      </div>
      <div className="rightcontainer">
        {member ? (
          <>
            <div className="header">{member?.name}</div>
            <div className="chatcontainer">
              <div className="messagecontainer"></div>
              <div className="inputcontainer">
                <textarea
                  className="messageinput"
                  onBlur={(evt) => setInput(evt.target.value)}
                />
                <button
                  className="messagesendbutton"
                  onClick={(evt) => sendInput(evt)}
                >
                  Send
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="conmessage">
            Please select user to start conversation{" "}
          </div>
        )}
      </div>
    </div>
  );
}

export default Chatroom;

