import React, { useEffect, useState } from "react";

function Chatroom({ logdinUser }) {
  const [users, setUsers] = useState([]);
  const [member, setMember] = useState();
  useEffect(() => {
    getUsers();
  }, []);
  const getUsers = async () => {
    try {
      const response = await fetch("/getUsers", {
        method: "GET",
      })
        .then((results) => results.json())
        .then((data) => {
          setUsers(data.users._embedded.data.users);
          console.log(
            "users : " + JSON.stringify(data.users._embedded.data.users)
          );
        });
    } catch (error) {
      console.error("Error in :", error);
    }
  };

  const ncard = (val) => {
    return <Card username={val.name} onClick={(val) => setMember(val)} />;
  };

  const Card = (props) => {
    const handleCardClick = () => {
      console.log("Card clicked:", props.username);
      props.onClick(); // Call the onClick function if provided
    };

    return (
      <div className="Card" onClick={handleCardClick}>
        <div className="card_info">{props.username}</div>
      </div>
    );
  };

  return (
    <div className="chatroom">
      <div className="leftroom">
        <div className="header">{logdinUser}</div>
        <div className="leftcontainer">{users.map(ncard)}</div>
      </div>
      <div className="rightcontainer">
        <div className="header">{member.name}</div>
        <div className="chatcontainer">
          <div className="messagecontainer"></div>
          <div className="inputcontainer">
            <input className="messageinput" />
            <button className="messagesendbutton">Send</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chatroom;

