import React, { useState, useEffect } from "react";
import nexmoClient from "nexmo-client";
import "./App.scss";
import User from "./components/User";
import Chatroom from "./components/Chatroom";

const App = () => {
  const [show, setShow] = useState(false);
  const [openConv, setOpenConv] = useState(false);
  const [userLoginInfo, setUserLoginInfo] = useState({});
  const login = async () => {
    try {
      let nexmo = new nexmoClient({
        debug: true,
        log_reporter: { enabled: true },
      });
      const sessionData = await nexmo.createSession(userLoginInfo.token);
      console.log("session" + sessionData);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        sessionData: sessionData,
      }));
    } catch (error) {
      console.error("Error in usersData:", error);
    }
  };

  const createConversation = () => {
    sessionData.newConversation().then((conv) => {
      conv.on("text", onMessage);
      conv.join().then(() => {
        setUserLoginInfo((prevState) => ({
          ...prevState,
          conversation: conv,
        }));
      });
    });
  };

  const getJWT = async (user) => {
    try {
      const response = await fetch("/getJWT", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: user.username }),
      });
      const data = await response.json();
      console.log("data" + data.jwt);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        token: data.jwt,
      }));
    } catch (error) {
      console.error("Error in getJWT:", error);
    }
  };

  const openChat = (user) => {
    setUserLoginInfo((prevState) => ({
      ...prevState,
      user: user,
    }));
    getJWT(user);
    setOpenConv(true);
  };

  const handleLoginClick = () => {
    setShow("login");
  };
  const handleSignupClick = () => {
    setShow("signup");
  };

  useEffect(() => {
    if (userLoginInfo.token) {
      login();
    }
  }, [userLoginInfo.token]);
  if (openConv) {
    return <Chatroom userLoginInfo={userLoginInfo} />;
  } else {
    return (
      <div className="nexmoapplication">
        <div className="nexmocontainer">
          <div className="btncontainer">
            <button className="button" onClick={handleLoginClick}>
              Login
            </button>
            <button className="button" onClick={handleSignupClick}>
              Signup
            </button>
          </div>
          {show == "signup" ? (
            <div className="signup">
              <User openChatUpdate={openChat} />
            </div>
          ) : (
            <div className="login">
              <h2>Comming soon</h2>
              <p>Login Component</p>
            </div>
          )}
        </div>
      </div>
    );
  }
};

export default App;
