import React, { useState } from "react";

const User = ({ onUpdate }) => {
  const [user, setUser] = useState({});

  const createUser = () => {
    const handleSubmit = async (e) => {
      e.preventDefault();
      const email = e.target[0].value;
      const password = e.target[1].value;

      try {
        await signInWithEmailAndPassword(auth, email, password);
        navigate("/");
      } catch (err) {
        setErr(true);
      }
    };

    fetch("/createUser", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: user.username }),
    })
      .then((results) => results.json())
      .then((data) => {
        setUser((prevUser) => ({
          ...prevUser,
          userId: data.id,
        }));
        onUpdate(user);
      });
  };

  if (user.userId) {
    return (
      <div className="conversation">
        Connected as <span className="conversation">{user.username}</span>
      </div>
    );
  } else {
    return (
      <div className="userinfo">
        <input
          id="username"
          className="userinput"
          placeholder="Enter your username"
          type="text"
          onChange={(evt) => setUser(evt.target.value)}
          value={user}
        />
        <button
          id="btn"
          className="button"
          target="_blank"
          onClick={createUser}
        >
          Create user
        </button>
      </div>
    );
  }
};

export default User;
