import React, { useEffect, useState } from "react";

function Chatroom() {
  const [users, setUsers] = useState();
  useEffect(() => {
    getUsers();
  }, []);
  const getUsers = async () => {
    try {
      const response = await fetch("/getUsers", {
        method: "GET",
      })
        .then((results) => results.json())
        .then((data) => {
          setUsers(data.users);
          console.log(
            "users : " + JSON.stringify(data.users._embedded.data.users)
          );
        });
    } catch (error) {
      console.error("Error in :", error);
    }
  };
  return (
    <div className="chatroom">
      <div className="leftcontainer">{Data.map(ncard)}</div>
      <div className="rightcontainer"></div>
    </div>
  );
}

const ncard = (val) => {
  return <Card username={val.username} />;
};

const Card = (props) => {
  return (
    <div className="Card">
      <div className="card_info">{props.username}</div>
    </div>
  );
};

export default Chatroom;

