import React, { useState, useEffect } from "react";
import nexmoClient from "nexmo-client";
import "./App.scss";
import User from "./components/User";
import Conversation from "./components/Conversation";
import Chatroom from "./components/Chatroom";

const App = () => {
  const [show, setShow] = useState(false);
  const [openConv, setOpenConv] = useState(true);
  const [userLoginInfo, setUserLoginInfo] = useState({});
  const [user, setUser] = useState();

  const getJWT = async (username) => {
    try {
      const response = await fetch("/getJWT", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: username }),
      });
      const data = await response.json();
      console.log("data" + data.jwt);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        token: data.jwt,
      }));
    } catch (error) {
      console.error("Error in getJWT:", error);
    }
  };

  const userUpdated = (user) => {
    setUser(user);
    if (user.username) {
      getJWT(user.username);
    }
  };

  const openChat = () => {
    setOpenConv(true);
    login();
  };

  const handleLoginClick = () => {
    setShow("login");
  };
  const handleSignupClick = () => {
    setShow("signup");
  };

  if (openConv) {
    return <Chatroom logdinUser={user} token={userLoginInfo.token} />;
  } else {
    return (
      <div className="nexmoapplication">
        <div className="nexmocontainer">
          <div className="btncontainer">
            <button className="button" onClick={handleLoginClick}>
              Login
            </button>
            <button className="button" onClick={handleSignupClick}>
              Signup
            </button>
          </div>
          {show == "signup" ? (
            <div className="signup">
              <User onUpdate={userUpdated} openChatUpdate={openChat} />
              {/* <Conversation
              sessionData={userLoginInfo.sessionData}
              loggedIn={userLoginInfo.token}
            /> */}
            </div>
          ) : (
            <div className="login">
              <h2>Comming soon</h2>
              <p>Login Component</p>
            </div>
          )}
        </div>
      </div>
    );
  }
};

export default App;
