import React, { useState, useEffect } from "react";

const Conversation = ({ app, loggedIn }) => {
  const [conversationData, setConversationData] = useState({});

  const createConversation = () => {
    app.newConversation().then((conv) => {
      conv.join().then((member) => {
        setConversationData({
          conversation: conv,
        });
      });
    });
  };

  useEffect(() => {
    // componentDidMount logic can go here
  }, []);

  if (conversationData.conversation) {
    return <div className="conversation">Joined conversation!</div>;
  } else {
    return (
      <div className="conversation">
        <button onClick={createConversation} disabled={!loggedIn}>
          Start conversation
        </button>
      </div>
    );
  }
};

export default Conversation;

