import React, { useState, useEffect } from "react";
import User from "./User";
import Conversation from "./Conversation";
import nexmoClient from "nexmo-client";

const App = () => {
  const [userLoginInfo, setUserLoginInfo] = useState({});

  const login = async () => {
    console.log("inside login" + userLoginInfo.token);
    let nexmo = new nexmoClient();
    try {
      const app = await nexmo.createSession(userLoginInfo.token);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        app: app,
      }));
    } catch (error) {
      console.error("Error in login:", error);
    }
  };

  const getJWT = async (username) => {
    try {
      const response = await fetch("/getJWT", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: username }),
      });
      const data = await response.json();
      console.log("data" + data);
      setUserLoginInfo((prevState) => ({
        ...prevState,
        token: data.jwt,
      }));
      console.log("inside jwt" + userLoginInfo.token);
      await login(); // Wait for login to complete before proceeding
    } catch (error) {
      console.error("Error in getJWT:", error);
    }
  };

  const userUpdated = (user) => {
    if (user.username) {
      getJWT(user.username);
    }
  };

  useEffect(() => {
    // Use useEffect to watch for changes in userLoginInfo.token
    if (userLoginInfo.token) {
      login();
    }
  }, [userLoginInfo.token]);

  return (
    <div className="nexmo">
      <User onUpdate={userUpdated} />
      <Conversation app={userLoginInfo.app} loggedIn={!!userLoginInfo.token} />
    </div>
  );
};

export default App;
