import React, { useState } from "react";

const User = ({ onUpdate }) => {
  const [user, setUser] = useState({});

  const createUser = () => {
    fetch("/createUser", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: state.username }),
    })
      .then((results) => results.json())
      .then((data) => {
        setUser({ userId: data.id });
        onUpdate(state);
        console.log("ooobhgencho" + state.userId);
      });
  };

  const setUsername = (evt) => {
    setUser({
      username: evt.target.value,
    });
  };

  if (user.userId) {
    return (
      <div className="userinfo userconnected">
        Connected as <span className="username">{user.username}</span>
      </div>
    );
  } else {
    return (
      <div className="userinfo">
        <input type="text" onChange={(evt) => setUsername(evt)} />
        <button onClick={createUser}>Create user</button>
      </div>
    );
  }
};

export default User;
